package FactoryMethodPattern;public class SpendFactory {    public Spender product(String type){        if ("message".equals(type))            return new SpendMassage();        else if ("mail".equals(type))            return new SpendEmail();        else            return null;    }    public Spender productMailSpender(){        return new SpendEmail();    }    public Spender productMessageSpender(){        return new SpendMassage();    }    public static Spender staticSpendMail(){        return new SpendEmail();    }    public static Spender staticSpendMessage(){        return new SpendMassage();    }}