package ProxyPattern;public class ProxySubject implements Subject{    private RealSubject realSubject;    public void request() {        if (realSubject == null){            realSubject = new RealSubject();        }        preRequest();        realSubject.request();        postRequest();    }    private void preRequest(){        System.out.println("这是前置处理...");    }    private void postRequest(){        System.out.println("这是后置处理...");    }}